        assembly ("memory-safe") {
            value := mload(add(add(buffer, 0x20), offset))
        }
    }
}

contracts/ArcMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title ArcMarketplace
 * @dev Marketplace simples para NFTs na Arc Network
 */
contract ArcMarketplace is Ownable, ReentrancyGuard {

    struct Listing {
        address seller;
        address nftContract;
        uint256 tokenId;
        uint256 price;
        bool isActive;
        uint256 createdAt;
    }

    // Mapping de listingId para Listing
    mapping(uint256 => Listing) public listings;
    uint256 public listingCount;

    // Taxa do marketplace (em basis points, 250 = 2.5%)
    uint256 public marketplaceFee = 250;
    uint256 public constant MAX_FEE = 1000; // 10% máximo

    // Token de pagamento (USDC na Arc Network)
    IERC20 public paymentToken;
    bool public useNativeToken = true; // Se true, usa ETH/USDC nativo

    // Eventos
    event Listed(
        uint256 indexed listingId,
        address indexed seller,
        address indexed nftContract,
        uint256 tokenId,
        uint256 price
    );

    event Sale(
        uint256 indexed listingId,
        address indexed buyer,
        address indexed seller,
        uint256 price,
        uint256 fee
    );

    event ListingCancelled(uint256 indexed listingId);
    event PriceUpdated(uint256 indexed listingId, uint256 newPrice);
    event FeeUpdated(uint256 newFee);

    constructor() Ownable(msg.sender) {}

    /**
     * @dev Listar um NFT para venda
     */
    function listNFT(
        address nftContract,
        uint256 tokenId,
        uint256 price
    ) external returns (uint256) {
        require(price > 0, "Price must be greater than 0");

        IERC721 nft = IERC721(nftContract);
        require(nft.ownerOf(tokenId) == msg.sender, "Not the owner");
        require(
            nft.isApprovedForAll(msg.sender, address(this)) ||
            nft.getApproved(tokenId) == address(this),
            "Marketplace not approved"
        );

        listingCount++;

        listings[listingCount] = Listing({
            seller: msg.sender,
            nftContract: nftContract,
            tokenId: tokenId,
            price: price,
            isActive: true,
            createdAt: block.timestamp
        });

        emit Listed(listingCount, msg.sender, nftContract, tokenId, price);

        return listingCount;
    }

    /**
     * @dev Comprar um NFT listado
     */
    function buyNFT(uint256 listingId) external payable nonReentrant {
        Listing storage listing = listings[listingId];

        require(listing.isActive, "Listing not active");
        require(listing.seller != msg.sender, "Cannot buy your own NFT");

        uint256 price = listing.price;
        uint256 fee = (price * marketplaceFee) / 10000;
        uint256 sellerAmount = price - fee;

        if (useNativeToken) {
            require(msg.value >= price, "Insufficient payment");

            // Transferir para o vendedor
            payable(listing.seller).transfer(sellerAmount);

            // Taxa fica no contrato

            // Devolver excesso
            if (msg.value > price) {
                payable(msg.sender).transfer(msg.value - price);
            }
        } else {
            require(
                paymentToken.transferFrom(msg.sender, listing.seller, sellerAmount),
                "Payment to seller failed"
            );
            require(
                paymentToken.transferFrom(msg.sender, address(this), fee),
                "Fee payment failed"
            );
        }

        // Transferir NFT
        IERC721(listing.nftContract).safeTransferFrom(
            listing.seller,
            msg.sender,
            listing.tokenId
        );

        listing.isActive = false;

        emit Sale(listingId, msg.sender, listing.seller, price, fee);
    }

    /**
     * @dev Cancelar uma listagem
     */
    function cancelListing(uint256 listingId) external {
        Listing storage listing = listings[listingId];

        require(listing.isActive, "Listing not active");
        require(listing.seller == msg.sender || msg.sender == owner(), "Not authorized");

        listing.isActive = false;

        emit ListingCancelled(listingId);
    }

    /**
     * @dev Atualizar preço de uma listagem
     */
    function updatePrice(uint256 listingId, uint256 newPrice) external {
        Listing storage listing = listings[listingId];

        require(listing.isActive, "Listing not active");
        require(listing.seller == msg.sender, "Not the seller");
        require(newPrice > 0, "Price must be greater than 0");

        listing.price = newPrice;

        emit PriceUpdated(listingId, newPrice);
    }

    /**
     * @dev Atualizar taxa do marketplace (apenas owner)
     */
    function setMarketplaceFee(uint256 newFee) external onlyOwner {
        require(newFee <= MAX_FEE, "Fee too high");
        marketplaceFee = newFee;
        emit FeeUpdated(newFee);
    }

    /**
     * @dev Configurar token de pagamento
     */
    function setPaymentToken(address token) external onlyOwner {
        paymentToken = IERC20(token);
        useNativeToken = false;
    }

    /**
     * @dev Usar token nativo para pagamentos
     */
    function setUseNativeToken(bool _useNative) external onlyOwner {
        useNativeToken = _useNative;
    }

    /**
     * @dev Retirar taxas acumuladas
     */
    function withdrawFees() external onlyOwner {
        if (useNativeToken) {
            uint256 balance = address(this).balance;
            require(balance > 0, "No fees to withdraw");
            payable(owner()).transfer(balance);
        } else {
            uint256 balance = paymentToken.balanceOf(address(this));
            require(balance > 0, "No fees to withdraw");
            paymentToken.transfer(owner(), balance);
        }
    }

    /**
     * @dev Obter listagens ativas
     */
    function getActiveListings(uint256 offset, uint256 limit) 
        external 
        view 
        returns (Listing[] memory) 
    {
        uint256 count = 0;

        // Contar listagens ativas
        for (uint256 i = 1; i <= listingCount; i++) {
            if (listings[i].isActive) count++;
        }

        // Aplicar paginação
        uint256 resultCount = count > offset ? count - offset : 0;
        if (resultCount > limit) resultCount = limit;

        Listing[] memory result = new Listing[](resultCount);
        uint256 resultIndex = 0;
        uint256 skipped = 0;

        for (uint256 i = 1; i <= listingCount && resultIndex < resultCount; i++) {
            if (listings[i].isActive) {
                if (skipped < offset) {
                    skipped++;
                } else {
                    result[resultIndex] = listings[i];
                    resultIndex++;
                }
            }
        }

        return result;
    }

    /**
     * @dev Obter listagens de um vendedor
     */
    function getSellerListings(address seller) 
        external 
        view 
        returns (uint256[] memory) 
    {
        uint256 count = 0;

        for (uint256 i = 1; i <= listingCount; i++) {
            if (listings[i].seller == seller && listings[i].isActive) count++;
        }

        uint256[] memory result = new uint256[](count);
        uint256 resultIndex = 0;

        for (uint256 i = 1; i <= listingCount && resultIndex < count; i++) {
            if (listings[i].seller == seller && listings[i].isActive) {
                result[resultIndex] = i;
                resultIndex++;
            }
        }

        return result;
    }
}

contracts/ArcNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title ArcNFT
 * @dev NFT Collection para Arc Network
 */
contract ArcNFT is ERC721, ERC721URIStorage, ERC721Enumerable, Ownable {
    uint256 private _tokenIds;

    uint256 public constant MAX_SUPPLY = 10000;
    uint256 public mintPrice = 0.01 ether; // Ajustar para USDC na Arc
    bool public isMintingActive = true;

    mapping(address => uint256) public mintedPerWallet;
    uint256 public maxMintPerWallet = 10;

    event NFTMinted(address indexed to, uint256 indexed tokenId, string tokenURI);
    event MintingToggled(bool isActive);
    event MintPriceUpdated(uint256 newPrice);

    constructor() ERC721("Arc NFT Collection", "ARCNFT") Ownable(msg.sender) {}

    /**
     * @dev Mint de um NFT
     */
    function mint(string memory uri) external payable returns (uint256) {
        require(isMintingActive, "Minting is not active");
        require(_tokenIds < MAX_SUPPLY, "Max supply reached");
        require(msg.value >= mintPrice, "Insufficient payment");
        require(
            mintedPerWallet[msg.sender] < maxMintPerWallet,
            "Max mint per wallet reached"
        );

        _tokenIds++;
        uint256 newTokenId = _tokenIds;

        _safeMint(msg.sender, newTokenId);
        _setTokenURI(newTokenId, uri);

        mintedPerWallet[msg.sender]++;

        emit NFTMinted(msg.sender, newTokenId, uri);

        return newTokenId;
    }

    /**
     * @dev Mint gratuito pelo owner (airdrops)
     */
    function mintForAddress(address to, string memory uri) external onlyOwner returns (uint256) {
        require(_tokenIds < MAX_SUPPLY, "Max supply reached");

        _tokenIds++;
        uint256 newTokenId = _tokenIds;

        _safeMint(to, newTokenId);
        _setTokenURI(newTokenId, uri);

        emit NFTMinted(to, newTokenId, uri);

        return newTokenId;
    }

    /**
     * @dev Ativar/Desativar minting
     */
    function toggleMinting() external onlyOwner {
        isMintingActive = !isMintingActive;
        emit MintingToggled(isMintingActive);
    }

    /**
     * @dev Atualizar preço de mint
     */
    function setMintPrice(uint256 newPrice) external onlyOwner {
        mintPrice = newPrice;
        emit MintPriceUpdated(newPrice);
    }

    /**
     * @dev Atualizar limite de mint por wallet
     */
    function setMaxMintPerWallet(uint256 newMax) external onlyOwner {
        maxMintPerWallet = newMax;
    }

    /**
     * @dev Retirar fundos
     */
    function withdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No funds to withdraw");
        payable(owner()).transfer(balance);
    }

    /**
     * @dev Total de tokens mintados
     */
    function totalMinted() external view returns (uint256) {
        return _tokenIds;
    }

    // Overrides necessários para ERC721Enumerable e ERC721URIStorage
    function _update(address to, uint256 tokenId, address auth)
        internal
        override(ERC721, ERC721Enumerable)
        returns (address)
    {
        return super._update(to, tokenId, auth);
    }

    function _increaseBalance(address account, uint128 value)
        internal
        override(ERC721, ERC721Enumerable)
    {
        super._increaseBalance(account, value);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721URIStorage, ERC721Enumerable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

contracts/ArcToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title ArcToken
 * @dev Token ERC20 customizado para Arc Network
 */
contract ArcToken is ERC20, Ownable {
    uint256 public constant MAX_SUPPLY = 1000000 * 10**18; // 1 milhão de tokens

    mapping(address => bool) public minters;

    event MinterAdded(address indexed minter);
    event MinterRemoved(address indexed minter);

    constructor() ERC20("Arc Token", "ARC") Ownable(msg.sender) {
        // Mint inicial para o owner
        _mint(msg.sender, 100000 * 10**18); // 100k tokens
    }

    /**
     * @dev Adiciona um endereço como minter autorizado
     */
    function addMinter(address minter) external onlyOwner {
        require(minter != address(0), "Invalid minter address");
        minters[minter] = true;
        emit MinterAdded(minter);
    }

    /**
     * @dev Remove um minter autorizado
     */
    function removeMinter(address minter) external onlyOwner {
        minters[minter] = false;
        emit MinterRemoved(minter);
    }

    /**
     * @dev Mint de novos tokens (apenas minters autorizados ou owner)
     */
    function mint(address to, uint256 amount) external {
        require(
            minters[msg.sender] || msg.sender == owner(),
            "Not authorized to mint"
        );
        require(
            totalSupply() + amount <= MAX_SUPPLY,
            "Max supply exceeded"
        );
        _mint(to, amount);
    }

    /**
     * @dev Burn de tokens
     */
    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
    }
}

contracts/ArcVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title ArcVault
 * @dev Vault/Staking para Arc Network
 * Permite stake de tokens ARC com recompensas
 */
contract ArcVault is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // Token de staking
    IERC20 public stakingToken;

    // Token de recompensa (pode ser o mesmo)
    IERC20 public rewardToken;

    // Informações de stake por usuário
    struct StakeInfo {
        uint256 amount;
        uint256 rewardDebt;
        uint256 stakedAt;
        uint256 lastClaimAt;
    }

    mapping(address => StakeInfo) public stakes;

    // Configurações do vault
    uint256 public totalStaked;
    uint256 public rewardPerSecond;
    uint256 public accRewardPerShare;
    uint256 public lastRewardTime;

    // Período mínimo de stake (em segundos)
    uint256 public minStakePeriod = 7 days;

    // Taxa de saída antecipada (em basis points, 500 = 5%)
    uint256 public earlyWithdrawFee = 500;

    // Precisão para cálculos
    uint256 private constant PRECISION = 1e12;

    // Eventos
    event Staked(address indexed user, uint256 amount);
    event Unstaked(address indexed user, uint256 amount, uint256 fee);
    event RewardClaimed(address indexed user, uint256 amount);
    event RewardRateUpdated(uint256 newRate);
    event MinStakePeriodUpdated(uint256 newPeriod);
    event EarlyWithdrawFeeUpdated(uint256 newFee);

    constructor(address _stakingToken, address _rewardToken) Ownable(msg.sender) {
        stakingToken = IERC20(_stakingToken);
        rewardToken = IERC20(_rewardToken);
        lastRewardTime = block.timestamp;
    }

    /**
     * @dev Atualizar recompensas acumuladas
     */
    function updatePool() public {
        if (block.timestamp <= lastRewardTime) {
            return;
        }

        if (totalStaked == 0) {
            lastRewardTime = block.timestamp;
            return;
        }

        uint256 timeElapsed = block.timestamp - lastRewardTime;
        uint256 reward = timeElapsed * rewardPerSecond;

        accRewardPerShare += (reward * PRECISION) / totalStaked;
        lastRewardTime = block.timestamp;
    }

    /**
     * @dev Fazer stake de tokens
     */
    function stake(uint256 amount) external nonReentrant {
        require(amount > 0, "Amount must be greater than 0");

        updatePool();

        StakeInfo storage userStake = stakes[msg.sender];

        // Se já tem stake, calcular recompensas pendentes
        if (userStake.amount > 0) {
            uint256 pending = (userStake.amount * accRewardPerShare / PRECISION) - userStake.rewardDebt;
            if (pending > 0) {
                _safeRewardTransfer(msg.sender, pending);
                emit RewardClaimed(msg.sender, pending);
            }
        }

        // Transferir tokens para o vault
        stakingToken.safeTransferFrom(msg.sender, address(this), amount);

        // Atualizar stake do usuário
        userStake.amount += amount;
        userStake.rewardDebt = userStake.amount * accRewardPerShare / PRECISION;

        if (userStake.stakedAt == 0) {
            userStake.stakedAt = block.timestamp;
        }

        totalStaked += amount;

        emit Staked(msg.sender, amount);
    }

    /**
     * @dev Retirar tokens do stake
     */
    function unstake(uint256 amount) external nonReentrant {
        StakeInfo storage userStake = stakes[msg.sender];

        require(userStake.amount >= amount, "Insufficient staked amount");
        require(amount > 0, "Amount must be greater than 0");

        updatePool();

        // Calcular recompensas pendentes
        uint256 pending = (userStake.amount * accRewardPerShare / PRECISION) - userStake.rewardDebt;

        if (pending > 0) {
            _safeRewardTransfer(msg.sender, pending);
            emit RewardClaimed(msg.sender, pending);
        }

        // Calcular taxa de saída antecipada
        uint256 fee = 0;
        if (block.timestamp < userStake.stakedAt + minStakePeriod) {
            fee = (amount * earlyWithdrawFee) / 10000;
        }

        uint256 amountAfterFee = amount - fee;

        // Atualizar stake do usuário
        userStake.amount -= amount;
        userStake.rewardDebt = userStake.amount * accRewardPerShare / PRECISION;

        if (userStake.amount == 0) {
            userStake.stakedAt = 0;
        }

        totalStaked -= amount;

        // Transferir tokens
        stakingToken.safeTransfer(msg.sender, amountAfterFee);

        // Taxa vai para o owner
        if (fee > 0) {
            stakingToken.safeTransfer(owner(), fee);
        }

        emit Unstaked(msg.sender, amount, fee);
    }

    /**
     * @dev Reivindicar recompensas sem fazer unstake
     */
    function claimRewards() external nonReentrant {
        updatePool();

        StakeInfo storage userStake = stakes[msg.sender];

        require(userStake.amount > 0, "No stake found");

        uint256 pending = (userStake.amount * accRewardPerShare / PRECISION) - userStake.rewardDebt;

        require(pending > 0, "No rewards to claim");

        userStake.rewardDebt = userStake.amount * accRewardPerShare / PRECISION;
        userStake.lastClaimAt = block.timestamp;

        _safeRewardTransfer(msg.sender, pending);

        emit RewardClaimed(msg.sender, pending);
    }

    /**
     * @dev Ver recompensas pendentes
     */
    function pendingRewards(address user) external view returns (uint256) {
        StakeInfo storage userStake = stakes[user];

        if (userStake.amount == 0) {
            return 0;
        }

        uint256 _accRewardPerShare = accRewardPerShare;

        if (block.timestamp > lastRewardTime && totalStaked > 0) {
            uint256 timeElapsed = block.timestamp - lastRewardTime;
            uint256 reward = timeElapsed * rewardPerSecond;
            _accRewardPerShare += (reward * PRECISION) / totalStaked;
        }

        return (userStake.amount * _accRewardPerShare / PRECISION) - userStake.rewardDebt;
    }

    /**
     * @dev Calcular APY atual
     */
    function currentAPY() external view returns (uint256) {
        if (totalStaked == 0) {
            return 0;
        }

        uint256 yearlyRewards = rewardPerSecond * 365 days;
        return (yearlyRewards * 10000) / totalStaked; // Em basis points
    }

    /**
     * @dev Transferência segura de recompensas
     */
    function _safeRewardTransfer(address to, uint256 amount) internal {
        uint256 rewardBalance = rewardToken.balanceOf(address(this));

        // Se for o mesmo token, subtrair o staked
        if (address(rewardToken) == address(stakingToken)) {
            rewardBalance = rewardBalance > totalStaked ? rewardBalance - totalStaked : 0;
        }

        if (amount > rewardBalance) {
            rewardToken.safeTransfer(to, rewardBalance);
        } else {
            rewardToken.safeTransfer(to, amount);
        }
    }

    // ============ ADMIN FUNCTIONS ============

    /**
     * @dev Definir taxa de recompensa por segundo
     */
    function setRewardPerSecond(uint256 _rewardPerSecond) external onlyOwner {
        updatePool();
        rewardPerSecond = _rewardPerSecond;
        emit RewardRateUpdated(_rewardPerSecond);
    }

    /**
     * @dev Definir período mínimo de stake
     */
    function setMinStakePeriod(uint256 _minStakePeriod) external onlyOwner {
        minStakePeriod = _minStakePeriod;
        emit MinStakePeriodUpdated(_minStakePeriod);
    }

    /**
     * @dev Definir taxa de saída antecipada
     */
    function setEarlyWithdrawFee(uint256 _fee) external onlyOwner {
        require(_fee <= 2000, "Fee too high"); // Max 20%
        earlyWithdrawFee = _fee;
        emit EarlyWithdrawFeeUpdated(_fee);
    }

    /**
     * @dev Depositar tokens de recompensa
     */
    function depositRewards(uint256 amount) external onlyOwner {
        rewardToken.safeTransferFrom(msg.sender, address(this), amount);
    }

    /**
     * @dev Retirar tokens de recompensa excedentes
     */
    function withdrawExcessRewards(uint256 amount) external onlyOwner {
        uint256 rewardBalance = rewardToken.balanceOf(address(this));

        if (address(rewardToken) == address(stakingToken)) {
            require(rewardBalance - amount >= totalStaked, "Cannot withdraw staked tokens");
        }

        rewardToken.safeTransfer(owner(), amount);
    }

    /**
     * @dev Emergência: pausar e retirar todos os fundos
     */
    function emergencyWithdraw() external nonReentrant {
        StakeInfo storage userStake = stakes[msg.sender];

        uint256 amount = userStake.amount;
        require(amount > 0, "No stake found");

        userStake.amount = 0;
        userStake.rewardDebt = 0;
        userStake.stakedAt = 0;

        totalStaked -= amount;

        stakingToken.safeTransfer(msg.sender, amount);

        emit Unstaked(msg.sender, amount, 0);
    }
}

